function detectError(arr, nr) {
    console.log(arr);
    console.log(typeof arr);
    console.log(nr);
    let n = arr.length;
    let res = 0;
 
    for (let i = 0; i < nr; i++) {
        let val = 0;
        for (let j = 1; j < n + 1; j++) {
            if(j & (2**i) == (2**i)) {
                val = val ^ parseInt(arr[-1 * j]);
            }
        }
        res = res + val*(10**i);
    }
 
    return parseInt(res.toString(), 2);
}



